cards.src:
let svg = d3.create("svg").attr("viewBox" , "0 0 300 300");

svg.append("circle")
.attr("fill" , "red")
.attr("cx" , 150)
.attr("cy" , 150)
.attr("r" , 100)
.attr("data-hint" , "d3!")
.on("mouseover" , function(e){
  this.setAttribute("fill" , "royalblue")
})
.on("mouseout" , function(e){
  this.setAttribute("fill" , "red")
})
;

return svg.node();

